(559, 256, '_wp_attachment_metadata', 'a:28:{s:10:\"dataformat\";s:3:\"mp3\";s:8:\"channels\";i:2;s:11:\"sample_rate\";i:48000;s:7:\"bitrate\";d:128010.913121323217637836933135986328125;s:11:\"channelmode\";s:6:\"stereo\";s:12:\"bitrate_mode\";s:3:\"vbr\";s:8:\"lossless\";b:0;s:15:\"encoder_options\";s:3:\"VBR\";s:17:\"compression_ratio\";d:0.08334043823002813822231615858981967903673648834228515625;s:10:\"fileformat\";s:3:\"mp3\";s:8:\"filesize\";i:4533261;s:9:\"mime_type\";s:10:\"audio/mpeg\";s:6:\"length\";i:281;s:16:\"length_formatted\";s:4:\"4:41\";s:5:\"album\";s:43:\"Đừng Yêu Nữa, Em Mệt Rồi (Single)\";s:8:\"composer\";s:22:\"Nguyễn Phúc Thiện\";s:5:\"genre\";s:12:\"Nhạc Trẻ\";s:17:\"copyright_message\";s:4:\"Zing\";s:5:\"title\";s:34:\"Đừng Yêu Nữa, Em Mệt Rồi\";s:6:\"artist\";s:3:\"MIN\";s:4:\"band\";s:3:\"MIN\";s:12:\"track_number\";s:1:\"1\";s:16:\"encoder_settings\";s:13:\"Lavf57.66.102\";s:4:\"text\";s:11:\"mp3.zing.vn\";s:4:\"year\";s:4:\"2019\";s:20:\"unsynchronised_lyric\";s:2473:\"Verse 1 \r\nĐã bao giờ anh nghe, có vài điều khi yêu\r\nMột là ko nói dối, hai là ko nói dối nhiều lần\r\nCớ sao toàn thấy nc mắt\r\nCớ sao toàn những vết cắt\r\nCó lẽ nào đến lúc, khi hai ta đã hết yêu\r\n\r\nCứ đi rồi sẽ đến, cứ im lặng sẽ qua\r\nMột người như thế đấy\r\nYêu là yêu đến hết đời này\r\nEm không là thế giới ấy\r\nEm thật là nhỏ bé thôi\r\nTại sao em cứ ấp úng mãi không bao giờ nói ra \r\n\r\nChorus 1 \r\nCó lẽ em yêu anh nhiều quá\r\nNên là em chẳng dám buông ra\r\nCó lên anh thấy em mạnh mẽ\r\nNên là anh chẳng muốn vỗ về\r\nYêu anh bằng trọn con tim ấy \r\nĐớn đau nhiều lắm anh biết không?\r\nCứ cho đi rồi nhận ra tim em vỡ đôi thêm đau\r\n\r\nCó lẽ không nên yêu nhiều thế\r\nBởi tình yêu sẽ nói em nghe\r\nVới trái tim ưu tư vụn vỡ\r\nNên làm quen với những bơ vơ\r\nCó khi chỉ vì anh không xứng đáng với ba chữ YÊU nữa rồi\r\nThế thôi em xin dừng lại đừng yêu nữa nhé anh ơi\r\nEm mệt rồi…\r\n\r\nVerse 2\r\nCứ đi rồi sẽ đến, cứ im lặng sẽ qua\r\nMột người như thế đấy\r\nYêu là yêu đến hết đời này\r\nEm không là thế giới ấy\r\nEm thật là nhỏ bé thôi\r\nTại sao em cứ ấp úng mãi không bao giờ nói ra\r\n\r\nChorus 2\r\nCó lẽ em yêu anh nhiều quá\r\nNên là em chẳng dám buông ra \r\nCó lẽ anh thấy em mạnh mẽ\r\nNên là anh chẳng muốn vỗ về\r\nYêu anh bằng trọn con tim ấy \r\nĐớn đau nhiều lắm anh biết không?\r\nCứ cho đi rồi nhận ra tim em vỡ đôi thêm đau\r\n\r\nCó lẽ không nên yêu nhiều thế\r\nBởi tình yêu sẽ nói em nghe\r\nVới trái tim ưu tư vụn vỡ\r\nNên làm quen với những bơ vơ\r\nCó khi chỉ vì anh không xứng đáng với ba chữ YÊU nữa rồi\r\nThế thôi em xin dừng lại đừng yêu nữa nhé anh ơi\r\nEm mệt rồi…\r\n\r\nBridge\r\nĐôi khi em nghĩ\r\nThật lòng em muốn cám ơn anh vì đã bên em ngày qua\r\nDừng lại thôi chỉ mong tìm lại một chút bình yên \r\n\r\nChorus 3\r\nCó lẽ không nên yêu nhiều thế\r\nBởi tình yêu sẽ nói em nghe\r\nVới trái tim ưu tư vụn vỡ\r\nNên làm quen với những bơ vơ\r\nCó khi chỉ vì anh không xứng đáng với ba chữ YÊU nữa rồi\r\nThế thôi em xin dừng lại đừng yêu nữa nhé anh ơi\r\nEm mệt rồi…\n\";s:11:\"totaltracks\";s:1:\"1\";s:5:\"image\";a:3:{s:4:\"mime\";s:10:\"image/jpeg\";s:5:\"width\";i:240;s:6:\"height\";i:240;}}'),
